package Datatypes.java;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 3;
		int b = 8;
		System.out.println(b=x);
		
		System.out.println(b+=x);
		
		System.out.println(b-=x);
		
		System.out.println(b*=x);
		
		System.out.println(b/=x);
		
		System.out.println(b%=x);

	}

}
 