package Datatypes.java;

public class Assignment2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 15;
		int b = 8;
		System.out.println(b=a);
		System.out.println(b+=a);
		System.out.println(b-=a);
		System.out.println(b*=a);
		System.out.println(b/=a);
		System.out.println(b%=a);
		System.out.println(b=++a);
	}

}
