package Datatypes.java;

public class Doubleprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//addition
		double a = 27.50;
		double b = 8.5;
		double c= a+b;
		System.out.println(c);
		
		//subtraction
		double i = 27.50;
		double j = 8.00;
		double  z = i-j;
		System.out.println(z);
		
		//multiplucation
		double e = 27.50;
		double d = 11.00;
		double f= e*d;
		System.out.println(f);
		
		//division
		double r= 7.50;
		double h = 8.5;
		double s= r/h;
		System.out.println(s);
	}

}
