package Datatypes.java;

public class Floatprogram1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float e = 15;
		float f = 20;
		float t=e-f; 
		System.out.println(t);
		
		//subtraction
		float a = 2.0f;
		float b =10;
		float c=a-b;
		System.out.println(c);
		
		//multiplucation
		float g= 20;
		float h =10;
		float k=g*h;
		System.out.println(k);
		
		//division
		float i = 6;
		float j =10;
		float m=i/j;
		
		System.out.println(m);
		 

	}

}
